mapper.select:
SELECT result2.* FROM (SELECT result1.* , ROWNUM as rnum FROM (SELECT * FROM dadoc_challenges
WHERE category LIKE '%'||#{category}||'%'
) result1
ORDER BY num DESC) result2 WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE dadoc_challenges
SET title=#{title}, category=#{category}, description=#{description}, 
period=#{period}, startDate=#{startDate}, endDate=#{endDate}, image=#{image}
WHERE num = #{num}
